pageContext.rootQuery:
query DocumentQuery($uid: String!) {
  prismic {
    document(lang:"en-us", uid: $uid) {
      title
      description
      image
      imageSharp {
        publicURL
      }
      section {
        ... on PRISMIC_DocumentSection {
          sectionbody
          sectiontitle
        }
      }
      _meta {
        uid
        tags
        firstPublicationDate
        lastPublicationDate
      }
    }
  }
}
