pageContext.rootQuery:
query PostQuery($uid: String!) {
  prismic {
    post(lang:"en-us", uid: $uid) {
      title
      image
      imageSharp {
        publicURL
      }
      body {
        ... on PRISMIC_PostBodyImage {
          type
          label
          primary {
            imageBlock
            imageBlockSharp {
              publicURL
              childImageSharp {
                fluid {
                  ...GatsbyImageSharpFluid_withWebp
                }
              }
            }
          }
        }
        ... on PRISMIC_PostBodyText {
          type
          label
          primary {
            text
          }
        }
      }
      excerpt
      _meta {
        uid
        tags
        firstPublicationDate
        lastPublicationDate
      }
    }
  }
}
