content.text:
(function(){
  const t = window.performance && performance.timing;
  if (!t) {
    return;
  }
  const loadTime = (t.loadEventEnd - t.navigationStart) / 1000;
  alert(`This page loaded in ${loadTime} seconds`);
}())

pageContext.rootQuery:
{
  prismic {
    allPosts {
      edges {
        node {
          title
          content
          excerpt
          _meta {
            uid
            tags
            firstPublicationDate
            lastPublicationDate
          }
        }
      }
    }
  }
}
